import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from './Card'

const SHUFFLE_URL = "https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1";

function DeckOfCards() {
  const [deck, setDeck] = useState(null);
  const [cards, setCards] = useState([])
//   const deckId = useRef()

  async function drawCard() {
    const DRAW_URL = `https://deckofcardsapi.com/api/deck/${deck.deck_id}/draw/?count=1`
    console.log(DRAW_URL)
    const res = await axios.get(DRAW_URL)
    console.log(res)
    setCards([...cards, res.data.cards[0]])
  };

  useEffect(function fetchDeckOfCards() {
    async function fetchDeck() {
      const res = await axios.get(SHUFFLE_URL);
      setDeck(res.data);
    }
    fetchDeck();
  }, []);

  return (
    <div>
      {/* <DrawButton drawCard={drawCard} /> */}
      <button onClick={() => drawCard()}>Draw Card!</button>
      {cards.map(cards.length ? <Card value={1} suite={1} /> : 'No cards')}
    </div>
  );
};

export default DeckOfCards;
