import React, { useState, useEffect } from "react";
import axios from "axios";
import DrawButton from "./DrawButton";

const SHUFFLE_URL = "https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1";
const DRAW_URL = `https://deckofcardsapi.com/api/deck/${deck_id}/draw/?count=1`

function DeckOfCards() {
  const [deck, setDeck] = useState(null);
//   const deckId = useRef()

  function drawCard() {
    
  };

  // this is called after component is first added to DOM
  useEffect(function fetchDeckOfCards() {
    async function fetchDeck() {
      const deckResult = await axios.get(SHUFFLE_URL);
      setDeck(deckResult);
    }
    fetchDeck();
  }, []);


  return (
    <div>
      {/* <DrawButton drawCard={drawCard} /> */}
      {deck ? <li>{}</li> : <i>no deck</i>}
    </div>
  );
};

export default DeckOfCards;
