import React, {useState} from 'react'
// import './BoxList.css';
import Box from './BoxList'
import NewBoxForm from './NewBoxForm'
import { v4 as uuid } from 'uuid';

function BoxList() {
    const INITIAL_STATE = [
        { id: uuid(), boxColor: 'black', boxWidth: '100px',  boxHeight: '100px'},
    ]
    const [boxes, setBoxes] = useState(INITIAL_STATE);
    const addBox = (newBox) => {
        setBoxes(box => [...boxes, { ...newBox, id: uuid() }])
    }

    return (
        <>
        <h2>BoxList</h2>
        <NewBoxForm addBox={addBox}/>
{/* ({ id, backgroundColor, width, height}) */}
        <div>
            {boxes.map(b => <Box id={b.id} boxColor={b.backgroundColor} boxWidth={b.width} boxHeight={b.height} />)}
        </div>
        </>
  );
}

export default BoxList;
