import React, { useState } from 'react'
// import './BoxList.css';
import ToDo from './Todo'
import NewBoxForm from './NewBoxForm'
import { v4 as uuid } from 'uuid';

function ToDoList() {
    // test to see if div appear
    const INITIAL_STATE = [
        { id: uuid(), name: 'todo number 1'},
    ]
    const [todos, setTodos] = useState(INITIAL_STATE);
    // const addBox = (boxColor, boxHeight, boxWidth) => {
    //     setBoxes(boxes => [...boxes, { boxColor: boxColor, boxHeight: boxHeight, boxWidth: boxWidth, id: uuid() }])
    // }
    // const removeBox = (id) =>{
    //     setBoxes(boxes.filter( i => i['id'] !== id))
    // }

    return (
        <div>
            {/* <NewBoxForm addBox={addBox}/> */}

            <h2>ToDoList</h2>

            <div className='todos'>
                {todos.map(({ id, name}) =>
                    <ToDo id={id} name={name} />)}
            </div>
        </div>
  );
}

export default BoxList;
